$get_app_mix_file_uri = function ( string $file ) {
    $upload_dir = trailingslashit( wp_upload_dir()[ 'baseurl' ] );
    return "{$upload_dir}app-mix/dist/{$file}";
};

add_action( 'wp_enqueue_scripts', function () use ( $get_app_mix_file_uri ) {

    wp_enqueue_script( 'app-mix', $get_app_mix_file_uri( 'app.js' ), [], false, true );

} );
